INSERT INTO `bot_widget_text` (`id`, `key`, `title`, `body`, `status`, `created_at`, `updated_at`, `images`, `screens_id`, `sort`, `buttons`, `media`) VALUES (40, 'kozanyj-cehol-dla-macbook-12-dujmov1523888409', 'Кожаный чехол для MacBook 12 дюймов_1523888409', 'Кожаный чехол, созданный Apple, идеально подойдёт для MacBook 12 дюймов. Он изготовлен из мягкой кожи европейского производства, прострочен в тон основному оттенку и имеет подкладку из мягкой микрофибры. Чехол защитит ваш 12‑дюймовый MacBook даже во время зарядки устройства: благодаря уникальному дизайну его не нужно снимать', 1, 1523888574, 1523894105, NULL, 19, 3, NULL, NULL);
