INSERT INTO `bot_widget_text` (`id`, `key`, `title`, `body`, `status`, `created_at`, `updated_at`, `images`, `screens_id`, `sort`, `buttons`, `media`) VALUES (43, 'airport-time-capsule1523888964', 'AirPort Time Capsule_1523888964', '', 1, 1523888981, 1528201235, '5ad4b355245e9.png', 21, 1, NULL, NULL);
INSERT INTO `bot_widget_text` (`id`, `key`, `title`, `body`, `status`, `created_at`, `updated_at`, `images`, `screens_id`, `sort`, `buttons`, `media`) VALUES (44, 'airport-time-capsule1523888989', 'AirPort Time Capsule_1523888989', 'AirPort Time Capsule — это беспроводное устройство для создания резервных копий. Оно автоматически устанавливает связь с Time Machine, обеспечивая простое и удобное решение для резервного копирования. Жёсткий диск ёмкостью 2 ТБ или 3 ТБ позволяет создавать и хранить защищённые резервные копии ваших компьютеров Mac. AirPort Time Capsule — это ещё и полнофункциональная базовая станция Wi-Fi с технологией 802.11ac. Она даёт вам возможность настроить высокоскоростную беспроводную сеть и выполнять через неё резервное копирование компьютеров Mac. И всё это с помощью одного универсального устройства\r\n\r\nAirPort Time Capsule - 2 ТБ\r\nЦена: 23 990.00 pyб\r\n\r\nAirPort Time Capsule - 3 ТБ\r\n31 990.00 pyб', 1, 1523889031, 1523893420, NULL, 21, 2, NULL, NULL);
