INSERT INTO `bot_widget_text` (`id`, `key`, `title`, `body`, `status`, `created_at`, `updated_at`, `images`, `screens_id`, `sort`, `buttons`, `media`) VALUES (150, 'fitnes-zal1524054370', 'Фитнес-зал_1524054370', 'Cеть фитнес-клубов \"Зебра\" включает в себя более 40-ка клубов в Москве и других городах России бизнес, премиум и люкс класса\r\n\r\nКлубы нашей сети оказывают полный спектр услуг как фитнес, так и SPA направления\r\n\r\nФитнес-клубы Зебра добавят в Вашу жизнь белую полосу', 1, 1524054383, 1524054863, NULL, 92, 2, NULL, NULL);
INSERT INTO `bot_widget_text` (`id`, `key`, `title`, `body`, `status`, `created_at`, `updated_at`, `images`, `screens_id`, `sort`, `buttons`, `media`) VALUES (151, 'nasi-preimusestva1524055075', 'Наши преимущества_1524055075', 'БАССЕЙН:\r\nАквааэробика, инструктора, ОЛИМПИЙСКИЕ РАЗМЕРЫ!\r\n\r\nГРУППОВЫЕ ПРОГРАММЫ:\r\nЙога, аэробика, танцы, сайкл, Кроссфит!\r\n\r\nТРЕНАЖЕРНЫЙ ЗАЛ:\r\nКардио-зона, зона свободных весов. Тренажеры последнего поколения PRECOR и оборудование HOIST, LIFEFITNESS\r\n\r\nЕДИНОБОРСТВА:\r\nБокс, борьба, айкидо, джиу-джицу, грэпплинг, дзюдо, карате, кикбоксинг, MMA, самбо, тайский бокс и рукопашный бой - приходите! Есть даже панкратион!\r\n\r\nЦЕНЫ:\r\nМы даем Вам только самую честную и выгодную цену на все наши услуги! Действуют скидки на карты до 80%! Бесплатные месяцы и дни открытых дверей!\r\n\r\nИНДИВИДУАЛЬНЫЙ ПОДХОД:\r\nС удовольствием подберем под Вас подходящую эффективную программу. Проведем фитнес-тестирование у врача\r\n\r\n24 ЧАСА:\r\nКруглосуточный сервис европейского качества: бассейн, банный комплекс, салон красоты\r\n\r\nБАННЫЙ КОМПЛЕКС:\r\nХаммам, финская сауна, инфракрасная сауна, гидромассаж, спа', 1, 1524055234, 1528202096, NULL, 93, 1, NULL, NULL);
INSERT INTO `bot_widget_text` (`id`, `key`, `title`, `body`, `status`, `created_at`, `updated_at`, `images`, `screens_id`, `sort`, `buttons`, `media`) VALUES (152, 'novosti1524057115', 'Новости_1524057115', 'Новостной раздел фитнес клуба \"Зебра\"\r\n\r\nВ АПРЕЛЕ В СЕТИ ФИТНЕС-КЛУБОВ ЗЕБРА ПРОЙДУТ НОЧИ ОТКРЫТЫХ ДВЕРЕЙ!', 1, 1524057131, 1528202101, NULL, 94, 1, NULL, NULL);
INSERT INTO `bot_widget_text` (`id`, `key`, `title`, `body`, `status`, `created_at`, `updated_at`, `images`, `screens_id`, `sort`, `buttons`, `media`) VALUES (153, 'novosti-11524057277', 'Новости 1_1524057277', 'В апреле в сети фитнес-клубов Зебра пройдут Ночи Открытых Дверей!\r\n\r\nА это значит, что будут самые низкие цены на карты и услуги клуба!\r\n\r\nУспейте купить! Даты проведения:\r\n\r\n16, 17, 18 апреля Зебра Автозаводская http://www.fitnes.ru/avtozavodskaya\r\n18, 19, 20 апреля Зебра Ленинградка http://www.fitnes.ru/rechnoy-vokzal\r\n18, 19, 20 апреля Зебра Енисейская http://www.fitnes.ru/babushkinskaya\r\n20, 21, 22 апреля Зебра Митино http://www.fitnes.ru/content/ru/club-2008-11-26-18-27-01.html\r\n20, 21, 22 апреля Зебра Бутово http://www.fitnes.ru/content/ru/club-2011-10-11-11-06-34.html\r\n20, 21, 22 апреля Зебра Орджоникидзе  http://www.fitnes.ru/tulskaya\r\n20, 21, 22 апреля Зебра Печатники http://www.fitnes.ru/content/ru/pechatniki.html\r\n23, 24, 25 апреля Зебра Останкино  http://www.fitnes.ru/vdnh\r\n25, 26, 27 апреля Зебра Широкая http://www.fitnes.ru/shirokaya', 1, 1524057326, 1528202168, NULL, 95, 1, NULL, NULL);
