INSERT INTO `bot_widget_text` (`id`, `key`, `title`, `body`, `status`, `created_at`, `updated_at`, `images`, `screens_id`, `sort`, `buttons`, `media`) VALUES (204, 'informacia-o-kompanii1524225184', 'Информация о компании_1524225184', 'Агенство \"Инфомаркет\" — один из ведущих российских web-разработчиков\r\n\r\nС 2005 года разрабатываем и поддерживаем интернет-проекты различной сложности — от корпоративных и продуктовых сайтов до высокотехнологичных порталов и интегрированных web-систем\r\n\r\n\"Инфомаркет\" сочетает консалтинговую экспертизу, широкие технологические компетенции, богатый опыт в области дизайна и юзабилити.\r\nНаши услуги ориентированы на средний и крупный бизнес', 1, 1524225359, 1528273644, NULL, 127, 2, NULL, NULL);
INSERT INTO `bot_widget_text` (`id`, `key`, `title`, `body`, `status`, `created_at`, `updated_at`, `images`, `screens_id`, `sort`, `buttons`, `media`) VALUES (205, 'o-nas1524225483', 'О нас_1524225483', 'Персональный подход \r\n\r\nВдумчивый и разборчивый подход к каждому проекту дает гарантию результата\r\n\r\nУ нас работают только лучшие в своем деле\r\n\r\nУ нас разделение труда. Каждый делает только то, что умеет лучше всего. Результат — быстрая и качественная работа!\r\n\r\nЛичная ответственность Project Manager\'a\r\n\r\nВы можете познакомиться с вашим личным Руководителем проекта, приехав в офис студии Инфомаркет в Москве. Дизайн и функциональность сайта обсудим за чашкой ароматного кофе', 1, 1524225619, 1528274000, NULL, 132, 1, NULL, NULL);
INSERT INTO `bot_widget_text` (`id`, `key`, `title`, `body`, `status`, `created_at`, `updated_at`, `images`, `screens_id`, `sort`, `buttons`, `media`) VALUES (206, 'razdel-oplaty1524239305', 'Раздел оплаты_1524239305', 'С помощью чат-ботов Ваши клиенты могут оплачивать Ваши товары и услуги прямо из мессенджера 💳\r\n\r\nПозже Вы сами сможете попробовать произвести оплату, пройти по пути Вашего клиента\r\n\r\nКонечно же мы с Вас за это не будем брать деньги, Вам нужно будет всего л', 1, 1524239500, 1524577253, NULL, 133, 2, NULL, NULL);
