INSERT INTO `bot_widget_text` (`id`, `key`, `title`, `body`, `status`, `created_at`, `updated_at`, `images`, `screens_id`, `sort`, `buttons`, `media`) VALUES (255, 'cat-boty1528277838', 'Чат-боты_1528277838', 'Чат-бот — виртуальный собеседник, который встраивается в мессенджеры и помогает бизнесу быть ближе к клиентам. Это автоматизированная интеллектуальная система общения с пользователями.\r\nПолучи новых клиентов в бизнес в автоматическом режиме, используя передовые технологии, через популярные мессенджеры с гарантией результата за 7 дней после старта', 1, 1528277855, 1528277937, NULL, 157, 2, NULL, NULL);
INSERT INTO `bot_widget_text` (`id`, `key`, `title`, `body`, `status`, `created_at`, `updated_at`, `images`, `screens_id`, `sort`, `buttons`, `media`) VALUES (256, 'integracii1528277954', 'Интеграции_1528277954', '', 1, 1528277966, 1530619319, NULL, 158, 1, NULL, NULL);
