INSERT INTO `bot_widget_text` (`id`, `key`, `title`, `body`, `status`, `created_at`, `updated_at`, `images`, `screens_id`, `sort`, `buttons`, `media`) VALUES (259, 'analitika1528278137', 'Аналитика_1528278137', 'Настроим сбор и визуализацию любых показателей Бизнеса для простого отслеживания, анализа и прогнозирования.\r\nВнедряемые нами решения ежедневно выгружают данные из множества источников и помогают быстро перейти к их эффективному анализу:\r\n\r\n Делаем ваши продажи прозрачными и управляемыми\r\n Консолидируем финансовую отчетность\r\n Визуализируем структуру клиентской базы\r\n Создаём омниканальные воронки для анализа маркетинга﻿', 1, 1528278160, 1529321609, NULL, 159, 2, NULL, NULL);
